mapper.select:
select
	
from t_cms t

	
		and t.id = #{id}			
	
	
		and t.title like concat('%',#{title},'%')
	
	
		and t.content = #{content}			
	
	
		and t.author = #{author}			
	
	
		and t.imgs = #{imgs}			
	
	
		and t.links = #{links}			
	
	
		and t.attachment = #{attachment}			
	
	
		and t.sort = #{sort}			
	
	
		and t.column_id = #{columnId}			
	
	
		and t.create_user = #{createUser}			
	
	
		and t.create_date >= #{createDateStart}
	
	
		and t.create_date < #{createDateEnd} 
	
	
		and t.update_user = #{updateUser}			
	
	
		and t.update_date >= #{updateDateStart}
	
	
		and t.update_date < #{updateDateEnd}

select
	
from t_cms t
where t.id = #{value}

mapper.update:
update t_cms
title = #{title}, content = #{content}, author = #{author}, imgs = #{imgs}, links = #{links}, attachment = #{attachment}, sort = #{sort}, column_id = #{columnId}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate},
where id = #{id}
