mapper.select:
select
	
from y_shop t

	
		and t.id = #{id}			
	
	
		and t.create_user = #{createUser}			
	
	
		and t.create_date = #{createDate}			
	
	
		and t.update_user = #{updateUser}			
	
	
		and t.update_date = #{updateDate}			
	
	
		and t.name like concat('%',#{name},'%')
	
	
		and t.address like concat('%',#{address},'%')
	
	
		and t.longitude = #{longitude}			
	
	
		and t.latitude = #{latitude}			
	
	
		and t.description = #{description}

select
	
from y_shop t
where t.id = #{value}

mapper.update:
update y_shop
create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate}, name = #{name}, address = #{address}, longitude = #{longitude}, latitude = #{latitude}, description = #{description},
where id = #{id}
